-- ----------------------------
--  Table structure for `MemberAccount`
-- ----------------------------
DROP TABLE IF EXISTS `MemberAccount`;
CREATE TABLE `MemberAccount` (
  `seq` int(11) NOT NULL AUTO_INCREMENT COMMENT '시퀀스',
  `userId` varchar(50) NOT NULL DEFAULT '' COMMENT '사용자 아이디',
  `userPw` varchar(255) NOT NULL DEFAULT '' COMMENT '사용자 비밀번호',
  `exchangePw` varchar(255) NOT NULL DEFAULT '' COMMENT '출금 비밀번호',
  `nickname` varchar(30) NOT NULL DEFAULT '' COMMENT '닉네임',
  `point` varchar(100) DEFAULT '0' COMMENT '포인트',
  `cash` varchar(100) DEFAULT '0' COMMENT '캐쉬',
  `level` int(11) NOT NULL DEFAULT 0 COMMENT '레벨',
  `phone` varchar(20) DEFAULT 'NULL' COMMENT '핸드폰 번호',
  `email` varchar(100) DEFAULT NULL COMMENT '이메일',
  `blacklistType` int(11) DEFAULT 1 COMMENT '블랙리스트 타입 (1:일반, 2:주의, 3:심각)',
  `bankName` varchar(30) DEFAULT 'NULL' COMMENT '출금 은행명',
  `bankDepositName` varchar(30) DEFAULT 'NULL' COMMENT '출금 은행 예금주',
  `bankNumber` varchar(100) DEFAULT '''NULL''' COMMENT '출금 계좌번호',
  `affiliatedId` varchar(50) DEFAULT 'NULL' COMMENT '추천인 아이디',
  `apiGamePer` int(11) DEFAULT 0 COMMENT 'API 연동 게임 분배 퍼센트',
  `accountType` int(11) DEFAULT 1 COMMENT '계정 타입 (1:일반, 2:임시 )',
  `memberType` int(11) DEFAULT 1 COMMENT '회원종류 (1:일반, 2:총판, 3:부본, 4:대본 )',
  `profitMonth` varchar(100) DEFAULT '0' COMMENT '이달 수익',
  `profitCumulative` varchar(100) DEFAULT '0' COMMENT '누적 수익',
  `status` int(11) DEFAULT 1 COMMENT '계정 상태 (1:활동중, 2:일시정지, 3:사용제한, 4:탈퇴)',
  `memo` varchar(8000) DEFAULT 'NULL' COMMENT '메모',
  `ip` varchar(50) DEFAULT NULL COMMENT '최초 가입시 아이피',
  `lastLogin` datetime DEFAULT NULL COMMENT '최근접속일',
  `isDel` char(50) DEFAULT 'N' COMMENT '삭제 여부 (Y:삭제, N:미삭제)',
  `regDate` datetime DEFAULT NULL COMMENT '등록일',
  `editDate` datetime DEFAULT NULL COMMENT '수정일',
  PRIMARY KEY (`seq`)
) ENGINE=InnoDB AUTO_INCREMENT=132 DEFAULT CHARSET=utf8;

